mapper.select:
SELECT dono,mno,doname,docontents,dodate,dotype,doprogress,fno FROM document
WHERE doprogress in ('결재대기','진행중','반려')
AND mno = #{mno}

SELECT dono,mno,doname,docontents,dodate,dotype,doprogress,fno FROM document
WHERE doprogress in ('결재완료')
AND mno = #{mno}

SELECT dono,mno,doname,docontents,dodate,dotype,doprogress,fno FROM document
WHERE doprogress = #{apvP}
AND mno = #{mno}

SELECT COUNT(doprogress) FROM document
WHERE doprogress = #{apvP}
AND mno = #{mno}
