mapper.select:
SELECT distinct mno, mname, mdate, mphone, mrank, oname, msal FROM(SELECT (@R := @R+1) AS RN, mno, mname, mdate, mphone, mrank, oname, msal FROM(SELECT m.mno, m.mname, m.mdate, m.mphone, m.mrank, o.oname, m.msal FROM member m LEFT OUTER JOIN organization o ON m.ono = o.ono
WHERE m.mno LIKE CONCAT('%',#{keyword},'%') OR m.mname LIKE CONCAT('%',#{keyword},'%') OR m.mrank LIKE CONCAT('%',#{keyword},'%') OR o.oname LIKE CONCAT('%',#{keyword},'%') GROUP BY m.mno, m.mname, m.mrank
ORDER BY m.mno asc) A, (SELECT @R:=0) AS X LIMIT #{endNo})B WHERE RN >=#{startNo}

SELECT COUNT(*) FROM(SELECT o.ono as o_ono FROM member m LEFT OUTER JOIN organization o ON m.ono = o.ono
WHERE mno LIKE CONCAT('%',#{keyword},'%') OR mname LIKE CONCAT('%',#{keyword},'%') OR mrank LIKE CONCAT('%',#{keyword},'%') OR o.oname LIKE CONCAT('%',#{keyword},'%') GROUP BY m.mno, m.mname, m.mrank
)A

SELECT ono, FLOOR(AVG(msal))AS amsal, MAX(msal)AS mmsal  
FROM member
GROUP BY ono;

SELECT mrank, FLOOR(AVG(msal))AS amsal, MAX(msal)AS mmsal  
FROM member
GROUP BY mrank;

SELECT *
FROM member
WHERE mname = #{mname} 
AND memail = #{memail}

SELECT *
FROM member
WHERE mid = #{mid} 
AND memail = #{memail}

mapper.update:
UPDATE member
SET password = #{password}
WHERE mno = #{mno}
