mapper.select:

SELECT  m.mno , m.mname , m.mrank , m.memail , m.mphone , m.mmessage , o.oname ,o.ono
FROM member as m inner join organization as o on m.ono = o.ono
WHERE m.mno = #{mno}

mapper.update:
update member set
memail=#{memail},
mphone=#{mphone},
mmessage = #{mmessage} 
where mno = #{mno}

mapper.select:
   SELECT m.mno , m.mname ,m.mphone , m.memail , m.mrank , o.oname 
FROM member as m inner join organization as o on m.ono = o.ono
 WHERE m.mname = #{mname}

  SELECT m.mno , m.mname ,m.mphone , m.memail , m.mrank , o.oname 
FROM member as m inner join organization as o on m.ono = o.ono
 WHERE m.mrank = #{mrank}

  SELECT m.mno, m.mname ,m.mphone , m.memail , m.mrank , o.oname 
FROM member as m inner join organization as o m.ono = o.ono
  WHERE m.ono = #{ono}
