mapper.select:
select b.*, m.mname mname 
from board as b inner join member as m on b.mno = m.mno
where b.bono = #{bono}

mapper.update:
update board set
botitle=#{botitle},
bocontents=#{bocontents},
fno = #{fno},
bostatus = #{bostatus}
where bono = #{bono}

update board set
botitle=#{botitle},
bocontents=#{bocontents},
bostatus = #{bostatus}
where bono = #{bono}

update board set
bohits=bohits+1 
where bono = #{bono}

mapper.select:
select count(*) 
from reply 
where bono=#{bono}
